INSERT INTO `component_lowcode_segment_template` (`id`, `code`, `name`, `name_template`, `name_output_variable`, `content_template`, `reference_segment_template_id`, `output_type_dict_id`, `output_variable`, `share_variables`, `remark`, `level`, `parent_id`, `parent_id1`, `parent_id2`, `parent_id3`, `parent_id4`, `parent_id5`, `parent_id6`, `parent_id7`, `parent_id8`, `parent_id9`, `parent_id10`, `version`, `tenant_id`, `create_at`, `create_by`, `update_at`, `update_by`) VALUES (1625434873126268930, NULL, '测试', '### 名称测试\n我的编码为test,我的名称为测试 #(global.test)', 'testName', '### 测试共享变量\n#for(item : share.import)\n#(item)\n#end\n\n### 测试共享变量，引用模板中定义了 refrecedImport 共享变量，可以被直接合并，即直接使用\n#for(item : share.refrecedImport)\n#(item)\n#end\n\n\n### 测试全局系统变量\ncurrentDate: #(sys.currentDate)\ncurrentDateTime: #(sys.currentDateTime)\n\n### 测试全局自定义变量\nglobal.test = #(global.test)\n\n### 测试全局扩展变量\next.test = #(ext.test)\n\n\n### 测试子级输出变量\ntestChild1 = #(child.testChild1)\n\ntestChild2 = #(child.testChild2)\n\n### 输出路径输出变量\noutputFileParentAbsoluteDir = #(outputFileParentAbsoluteDir)\n\n### 测试temp\n#if(temp.put(\'javaPackage\', global.test + \'.\' + global.test + \'.\' + \'adapter\' ))#end\n#(temp)\n', 1625504497146605570, 1625787765335097345, NULL, 'import', '测试根模板，单独测试子级模板可能会系统异常，这是正常的，因为子级中可能引用了父级变量', 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 1, '2023-02-14 18:00:48', NULL, NULL, NULL);
