INSERT INTO `component_openplatform_doc_api_doc_template_example_code` (`id`, `lang_dict_id`, `example_code`, `example_download_url`, `openplatform_doc_api_doc_template_id`, `seq`, `remark`, `version`, `tenant_id`, `create_at`, `create_by`, `update_at`, `update_by`) VALUES (1772165527225970689, 1769655127657996290, 'import java.io.*;\nimport java.net.HttpURLConnection;\nimport java.net.MalformedURLException;\nimport java.net.URL;\nimport java.security.MessageDigest;\nimport java.security.NoSuchAlgorithmException;\nimport java.util.HashMap;\nimport java.util.Map;\nimport java.util.UUID;\n\n/**\n * <p>\n * 简单测试开放接口示例代码\n * </p>\n *\n * @author yangwei\n * @since 2024/7/12 18:05\n */\npublic class OpenapiSimpleTest {\n\n    /**\n     * 客户端id全局配置\n     */\n    private static String clientId = \"1688811472965656576\";\n    /**\n     * 客户端密码全局配置\n     */\n    private static String clientSecret = \"03b109817ddc4269a40d30ca12459511\";\n\n\n    public static void main(String[] args) {\n        String response = postTest();\n        // 打印一下结果\n        System.out.println(response);\n    }\n\n    /**\n     * 测试一下post方式请求\n     */\n    private static String postTest() {\n        // 请求参数 请求体\n        String body = postBody();\n        // 请求流水号\n        String nonce = UUID.randomUUID().toString();\n        // 请求时间戳\n        long timestamp = System.currentTimeMillis();\n        // sha256摘要\n        String sha256Hash = digestPostBody(nonce,timestamp,body);\n        // 请求头\n        Map<String, String> headers = headers(nonce, timestamp, sha256Hash);\n        // 接口地址，请根据实际情况填写，此处以工商简项为例\n        String apiUrl = \"http://localhost:8080/openapi/testPostBody\";\n        String response = doPost(apiUrl,body,headers);\n        return response;\n    }\n\n    /**\n     * 请求参数\n     * @return\n     */\n    private static String postBody() {\n        // 请求体\n        String body = \"{\\\"testField1\\\":\\\"测试字段1\\\"}\";\n        return body;\n    }\n\n    /**\n     * 计算摘要\n     * @param nonce\n     * @param timestamp\n     * @param body\n     * @return\n     */\n    private static String digestPostBody(String nonce,long timestamp,String body) {\n        StringBuffer sb = new StringBuffer();\n        sb.append(\"clientId\").append(\"=\").append(clientId);\n        sb.append(\"timestamp\").append(\"=\").append(timestamp);\n        sb.append(\"nonce\").append(\"=\").append(nonce);\n        sb.append(body);\n        sb.append(clientSecret);\n        String sha256Hash = getSHA256Hash(sb.toString());\n        return sha256Hash;\n    }\n\n    /**\n     * 请求头\n     * @param nonce\n     * @param timestamp\n     * @param sha256Hash\n     * @return\n     */\n    private static Map<String,String> headers(String nonce,long timestamp,String sha256Hash) {\n        Map<String,String> headers = new HashMap<String, String>();\n        headers.put(\"clientId\",clientId);\n        headers.put(\"timestamp\",timestamp + \"\");\n        headers.put(\"nonce\",nonce);\n        headers.put(\"signature\",sha256Hash);\n        return headers;\n    }\n    /**\n     * 请求数据\n     * @param httpUrl\n     * @param body\n     * @param headers\n     * @return\n     */\n    private static String doPost(String httpUrl, String body, Map<String,String> headers) {\n\n        HttpURLConnection connection = null;\n        InputStream is = null;\n        OutputStream os = null;\n        BufferedReader br = null;\n        String result = null;\n        try {\n            URL url = new URL(httpUrl);\n            connection = (HttpURLConnection) url.openConnection();\n            connection.setRequestMethod(\"POST\");\n            connection.setDoOutput(true);\n            connection.setRequestProperty(\"Content-Type\", \"application/json\");\n            for(Map.Entry<String,String> entry : headers.entrySet()){\n                connection.setRequestProperty(entry.getKey(),entry.getValue());\n            }\n            os = connection.getOutputStream();\n            os.write(body.getBytes(\"UTF-8\"));\n            try {\n                is = connection.getInputStream();\n            } catch (IOException e) {\n                is = connection.getErrorStream();\n            }\n            br = new BufferedReader(new InputStreamReader(is, \"UTF-8\"));\n\n            StringBuffer sbf = new StringBuffer();\n            String temp = null;\n            while ((temp = br.readLine()) != null) {\n                sbf.append(temp);\n                sbf.append(\"\\r\\n\");\n            }\n            result = sbf.toString();\n        } catch (MalformedURLException e) {\n            e.printStackTrace();\n        } catch (IOException e) {\n            e.printStackTrace();\n        } finally {\n            // 关闭资源\n            if (null != br) {\n                try {\n                    br.close();\n                } catch (IOException e) {\n                    e.printStackTrace();\n                }\n            }\n            if (null != os) {\n                try {\n                    os.close();\n                } catch (IOException e) {\n                    e.printStackTrace();\n                }\n            }\n            if (null != is) {\n                try {\n                    is.close();\n                } catch (IOException e) {\n                    e.printStackTrace();\n                }\n            }\n            connection.disconnect();\n        }\n        return result;\n    }\n\n    /**\n     * sha256摘要\n     * @param input\n     * @return\n     */\n    public static String getSHA256Hash(String input) {\n        try {\n            // 创建MessageDigest实例，初始化为SHA-256算法\n            MessageDigest md = MessageDigest.getInstance(\"SHA-256\");\n            // 计算input字符串的字节\n            byte[] messageDigest = md.digest(input.getBytes());\n            // 创建Hex String\n            StringBuilder hexString = new StringBuilder();\n            for (byte b : messageDigest) {\n                String hex = Integer.toHexString(0xff & b);\n                if(hex.length() == 1) hexString.append(\'0\');\n                hexString.append(hex);\n            }\n            // 返回32个字符的十六进制数作为SHA-256哈希\n            return hexString.toString();\n        }\n        // 处理NoSuchAlgorithmException异常\n        catch (NoSuchAlgorithmException e) {\n            throw new RuntimeException(e);\n        }\n\n    }\n}\n\n', NULL, 1770755486220496898, 10, 'NativeDemo.java', 3, NULL, '2024-03-25 15:36:01', 1, '2024-07-13 11:06:32', 1);
